import React, { Component } from 'react';
import DataTable from '../../services/reactTable';
import cloneDeep from 'clone-deep';
import { toPercentage } from '../../helper/formatter';
import ColumnSelection from '../../components/columnSelection';
import styled from 'styled-components';
import _ from 'lodash';

class MarkdownDetailedTable extends Component {
  constructor() {
    super();
    this.state = {
      columnOptions: [],
      selLabels: [],
      column: {
        Header: '',
        columns: [
          {
            Header: 'ST %',
            accessor: 'sellThrough',
            // minWidth: 600,
            width: 500,
            Cell: (props) => <div> {toPercentage(props)} </div>,
          },
          {
            Header: 'Margin %',
            accessor: 'Margin Percentage',
            // minWidth: 600,
            width: 500,
            Cell: (props) => <div> {toPercentage(props)} </div>,
          },
        ],
      },
      headers: [
        {
          Header: 'Style/Style Group',
          accessor: 'SKU_Id',
          width: 300,
        },
      ],
    };
  }
  onHandleCheckBox = (e, label, index) => {
    //adding
    if (e.target.checked) {
      if (this.state.selLabels.indexOf(label) === -1) {
        let labels = [...this.state.selLabels, label];
        let tempArr = [];
        labels.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });
        this.setState({
          selLabels: labels,
          columnOptions: tempArr,
        });
      }
    } else {
      //removing
      let index = this.state.selLabels.indexOf(label);
      if (index !== -1) {
        let selectedLabel = _.cloneDeep(this.state.selLabels);
        selectedLabel.splice(index, 1);
        let tempArr = [];
        selectedLabel.forEach((label) => {
          let columns = this.props.columnHeaders[label];
          let labelColumns = Object.keys(columns);
          let options = labelColumns.map((label) => {
            return {
              label: label,
              value: columns[label],
            };
          });
          tempArr.push(...options);
        });

        let selectedItemsClone = _.cloneDeep(this.state.selectedItems);
        let newSelectedItems = [];
        let labelArr = Object.keys(this.props.columnHeaders[label]);
        selectedItemsClone.forEach((item) => {
          if (!labelArr.includes(item.label)) {
            newSelectedItems.push(item);
          }
        });

        this.onHandleSelect(newSelectedItems);
        this.setState({
          selLabels: selectedLabel,
          columnOptions: tempArr,
          selectedItems: newSelectedItems,
        });
      }
    }
  };
  onHandleDropdownSelect = (options) => {
    let headers = cloneDeep(this.state.columns);
    _.remove(headers, (item) => item.metric);
    options.forEach((da) => {
      let newCols = headers.filter((a) => {
        return a.accessor === da.value;
      });
      if (newCols.length == 0) {
        headers.push({
          Header: da.label,
          accessor: da.value,
          metric: true,
        });
      }
    });
    this.setState({
      columns: headers,
      selectedItems: options,
    });
  };
  render() {
    console.log(this.props.columnHeaders, 'columnHeadersdetail');
    let headers = cloneDeep(this.state.headers);
    console.log(headers, 'heda');
    this.props.data.weeks.forEach((week) => {
      let column = cloneDeep(this.state.column);
      column.Header = week.toUpperCase();
      console.log(week, 'week');
      column.columns[0].accessor = `${week}${column.columns[0].accessor}`;
      column.columns[1].accessor = `${week}${column.columns[1].accessor}`;
      headers.push(column);
    });
    console.log('headers::::', headers, this.props.data);

    return (
      <div>
        <ColSelBtn>
          {this.props.columnHeaders && Object.keys(this.props.columnHeaders).length && (
            <ColumnSelection
              onHandleCheckBox={this.onHandleCheckBox}
              columnHeaders={this.props.columnHeaders}
              onHandleSelect={this.onHandleDropdownSelect}
              columnOptions={this.state.columnOptions}
              selectedItems={this.state.selectedItems}
              selLabels={this.state.selLabels}
            />
          )}
        </ColSelBtn>
        <div style={{ overflowX: 'auto', width: '100%', paddingBottom: '28px' }}>
          <DataTable columns={headers} data={this.props.data.data} isPagination={true} />
        </div>
      </div>
    );
  }
}

export default MarkdownDetailedTable;

const ColSelBtn = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;
